css:
.print-preview {
  padding: 0px !important;
  margin-left: 10px !important;
  margin-right: 10px !important;
}

.print-format {
    max-height: 8.3in !important;
    min-width: 11.69in !important;
    padding: 0.2in !important;
    margin: 0px !important;
}

.print-format {
    font-size: 7pt !important;
}

h1, .h1, h2, .h2, h3, .h3 {
    margin-top: 5px;
    margin-bottom: 6px;
}

.print-heading {
    padding-bottom: 10px;
    margin-bottom: -6px;
    border-bottom: 0px solid #d1d8dd;
}

@media print {
    @page {size: landscape}
    .print-format {
        max-height: 8.3in !important;
        min-width: 11.69in !important;
        padding: 0.2in !important;
        margin: 0px !important;
    }
    .print-format {
        font-size: 7pt !important;
    }
    
    h1, .h1, h2, .h2, h3, .h3 {
        margin-top: 5px;
        margin-bottom: 6px;
    }
    
    .print-heading {
        padding-bottom: 10px;
        margin-bottom: -6px;
        border-bottom: 0px solid #d1d8dd;
}

}

.print-format {
    font-size: 10pt !important;
}

h1, .h1, h2, .h2, h3, .h3 {
    margin-top: 5px;
    margin-bottom: 6px;
}

@media print {
    .print-format {
        font-size: 10pt !important;
    }
    
    h1, .h1, h2, .h2, h3, .h3 {
    margin-top: 5px;
    margin-bottom: 6px;
    }
}
